    negativeCounter: 0,
    positiveCounter: 0,
    descriptionArray: ["Bambi", "Sheep", "Lion"],
    chosenAnimal: null
  };
  choseRandomAnimal = () => {
    let num = Math.floor(Math.random() * (this.state.descriptionArray.length));
    console.log(`choseRandomAnimal = `);
    // console.log(`choseRandomAnimal = ${this.descriptionArray[num]}`);
  }


  
  render() {
    return (
      <div>
      <Counter negativeCounter = {this.state.negativeCounter} 
      positiveCounter = {this.state.positiveCounter}/>
      {this.choseRandomAnimal()}
      </div>
    );
  }
}

export default App;
