import React, { Component } from "react";

export default class AnswerButton extends Component {

  style = {true: <span role="img" aria-label="right">✅</span>, 
false: <span role="img" aria-label="wrong">❌</span>
}
  handleClick = (bool1) => {
    if (this.props.bool == bool1) console.log("yes")
    this.props.choseRandomAnimals();

  }
  render() {
    return (
      <div className="topMenu">
      <button onClick={() => this.handleClick(this.props.text)}>
      {this.style[this.props.text]}
      </button>
      </div>
    );
  }
}
